import { useRef } from "react";

const GetValueFormInput = () => {
    // const handleClick = (e, name) => {
    //     console.log("Welcome:" + name);
    //     console.log(e.target.innerText);
    // };

    // const handleChange = (e) => {   
    //     console.log(e.target.value);
    // }
    // return (
    //     <div>
    //         <button onClick={(e) => handleClick(e, 'FPTU')}>
    //             Click Me
    //         </button>
    //         <input onChange={(e) => handleChange(e)} />
    //     </div>
    // );
    const emailValue = useRef(null);
    const passValue = useRef(null);

    const handleSubmit = (event) => {
        event.preventDefault();
        // console.log(event.target[0].value);
        // console.log(event.target.elements.username.value);
        // console.log(event.target.username.value);
        // console.log(event.target[1].value);
        // console.log(event.target.elements.password.value);
        // console.log(event.target.password.value);
        // console.log(`Email: ${username}`);

        console.log(`Email: ${emailValue.current.value} Password: ${passValue.current.value}`);

    }

    return (
        <form onSubmit={(e) => handleSubmit(e)}>
            <div className="container">
                <div>
                    <label>Email(*)</label>
                    <input type='text' name="username" placeholder='Email' ref={emailValue}></input>
                </div>
                <div>
                    <label>Password</label>
                    <input type='password' name='password' placeholder="Password" ref={passValue}></input>
                </div>
            </div>
            <input type="submit" value="Sign In" style={{ marginRight: "5px" }}></input>
            <a href="/">Sign Up</a>
        </form>
    );
}

export default GetValueFormInput;
