import { useEffect, useState } from "react";
import { Table } from "react-bootstrap";

const LoadDataToTable = () => {

    const [persons, setPerson] = useState([]);
    const [type, setType] = useState('all');

    useEffect(() => {
        if (type !== 'all') {
            fetch(`https://dummyjson.com/users/${type}`)
                .then(res => res.json())
                .then(json => {
                    setPerson([json]);
                });
        } else {
            fetch("http://localhost:3004/users")
                .then(res => res.json())
                .then(json => {
                    setPerson(json.users);
                });
        }
    }, [type]);
    console.log(persons);
    return (
        <div>
            <button onClick={() => { setType(1) }}>
                User 1
            </button>
            <button onClick={() => { setType(2) }}>
                User 2
            </button>
            <button onClick={() => { setType(3) }}>
                User 3
            </button>
            <button onClick={() => { setType(4) }}>
                User 4
            </button>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>
                            Id
                        </th>
                        <th>
                            Name
                        </th>
                        <th>
                            Age
                        </th>
                        <th>
                            Action
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {
                        persons.map(({ id, firstName, age }) =>
                            <tr key={id}>
                                <td>
                                    {id}
                                </td>
                                <td>
                                    {firstName}
                                </td>
                                <td>
                                    {age}
                                </td>
                                <td>
                                    <button>
                                        Edit
                                    </button>
                                    <button>
                                        Delete
                                    </button>
                                </td>
                            </tr>)
                    }
                </tbody>
            </Table>
        </div>
    );
}

export default LoadDataToTable;
